mapper.update:
UPDATE MVY_MOVIE
MOVIE_TITLE = #{movieTitle}, MOVIE_PRDTYEAR = #{prdtYear}, MOVIE_NATION = #{nation}, MOVIE_DIRECTOR = #{director},
GENRE_NO = #{genreNo}, GENRE_NAME = #{genreName},
MOVIE_RUNTIME = #{runtime}, MOVIE_GRADE = #{grade}, MOVIE_PRICE = #{price}, MOVIE_STORY = #{movieStory}
WHERE MOVIE_NO = #{movieNo}

mapper.select:
SELECT IDX, STORED_FILE_NAME
FROM MVY_MOVIE_PHOTO
WHERE PARENT_SEQ = #{parentSeq}
